import React from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import axios from "axios";

const schema = yup.object().shape({
  newPassword: yup.string().min(6).required("New password is required"),
});

const SuccessAndUpdatePage = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({ resolver: yupResolver(schema) });

  const onSubmit = async (data) => {
    try {
      const res = await axios.post("http://localhost:5000/api/update-password", data);
      console.log(res.data);
    } catch (err) {
      console.error(err.response?.data || err.message);
    }
  };

  return (
    <div className="container mt-5">
      <h2>Login Success</h2>
      <p>You are now logged in!</p>

      <h4 className="mt-4">Update Password</h4>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="mb-3">
          <label>New Password</label>
          <input {...register("newPassword")} type="password" className="form-control" />
          <p className="text-danger">{errors.newPassword?.message}</p>
        </div>
        <button className="btn btn-dark">Update Password</button>
      </form>
    </div>
  );
};

export default SuccessAndUpdatePage;
